mapper.select:
select student.name
from db_wangdao_homework.student
where id = #{param1};

select student.name
from db_wangdao_homework.student
where id = #{id};

select student.name
from db_wangdao_homework.student
where id = #{param1}
   or name = #{param2};

select *
from db_wangdao_homework.student
where name = #{name}
   or age = #{age};

select *
from db_wangdao_homework.student
where name = #{student.name}
   or age = #{student.age};
